import base64
from unicodedata import decimal
import codecs
encodedStr = ""

# bas64 --> binary
sample_string = base64.b64decode(encodedStr).decode('ascii')


# binary ---> hex
mess = hex(int(sample_string,2))
hexMess =''
for i in range(4,len(mess),4):
    hexMess += mess[i:i+2]
    
# print(hexMess)

# hex ---> str(dec):
decimalMess = bytes.fromhex(hexMess).decode('ascii')
print(decimalMess)

# dec ---> text:
text = ''
index = 0
while index < len(decimalMess):
    number = decimalMess[index:index+2]
    if int(number) < 32: 
        number = decimalMess[index:index+3]
        text += chr(int(number))
        index = index+3
    else:
        text += chr(int(number))
        index = index+2
print(text)

# decode ROT13
messRot13 = codecs.decode(text,'rot_13')
print(messRot13)

# decode Base64 ---> flag:
flag = base64.b64decode(messRot13).decode('ascii')
print(flag)






    
